tegory.reset_index() # Daily data by region daily_region = df_integrated.groupby(['Date', 'region']).agg({ 'interest': ['mean', 'sum', 'count'], 'volatility_7d': 'mean' }).round(3) daily_region.columns = ['avg_interest', 'total_interest', 'observation_count', 'avg_volatility'] daily_region = daily_region.reset_index() # Weekly data (promedio por semana) df_integrated['week'] = df_integrated['Date'].dt.isocalendar().week df_integrated['year'] = df_integrated['Date'].dt.year weekly_data = df_integrated.groupby(['year', 'week', 'content_category']).agg({ 'interest': ['mean', 'sum'], 'volatility_7d': 'mean', 'Close': 'mean' }).round(3) weekly_data.columns = ['avg_interest', 'total_interest', 'avg_volatility', 'avg_price'] weekly_data = weekly_data.reset_index() # Calculate temporal metrics print(" Calculando mÃ©tricas temporales...") # Trends by category category_trends = {} for category in df_integrated['content_category'].unique(): cat_data = daily_category[daily_category['content_category'] == category] if len(cat_data) > 1: # Calcular tendencia (pendiente de regresiÃ³n lineal simple) x = np.arange(len(cat_data)) y = cat_data['avg_interest'].values trend = np.polyfit(x, y, 1)[0] # Pendiente category_trends[category] = { 'trend': float(trend), 'start_value': float(y[0]), 'end_value': float(y[-1]), 'change_percent': float((y[-1] - y[0]) / y[0] * 100) if y[0] != 0 else 0 } # Save temporal data print(" Guardando datos temporales...") # Save daily data by category daily_category_output = output_dir / "daily_category_data.csv" daily_category.to_csv(daily_category_output, index=False) print(f"OK: Daily data by category guardados: {daily_category_output}") # Save daily data by region daily_region_output = output_dir / "daily_region_data.csv" daily_region.to_csv(daily_region_output, index=False) print(f"OK: Daily data by region guardados: {daily_region_output}") # Save weekly data weekly_output = output_dir / "weekly_data.csv" weekly_data.to_csv(weekly_output, index=False) print(f"OK: Weekly data guardados: {weekly_output}") # Create summary temporal temporal_summary = { "date_range": { "start": str(df_integrated['Date'].min()), "end": str(df_integrated['Date'].max()), "total_days": int((df_integrated['Date'].max() - df_integrated['Date'].min()).days) }, "category_trends": category_trends, "total_observations": len(df_integrated), "avg_daily_interest": float(daily_category['avg_interest'].mean()), "max_daily_interest": float(daily_category['avg_interest'].max()), "min_daily_interest": float(daily_category['avg_interest'].min()) } summary_output = output_dir / "temporal_summary.json" with open(summary_output, 'w') as f: json.dump(temporal_summary, f, indent=2) print(f"OK: Temporal summary guardado: {summary_output}") print("\n TEMPORAL PREPARATION SUMMARY:") print(f" â€¢ Date range: {temporal_summary['date_range']['start']} a {temporal_summary['date_range']['end']}") print(f" â€¢ Total de dÃ­as: {temporal_summary['date_range']['total_days']}") print(f" â€¢ Total observations: {temporal_summary['total_observations']}") print(f" â€¢ Daily average interest: {temporal_summary['avg_daily_interest']:.2f}") print("\n TRENDS BY CATEGORY:") for category, trend_data in category_trends.items(): direction = "" if trend_data['trend'] > 0 else "ğŸ“‰" if trend_data['trend'] < 0 else "â¡ï¸" print(f" {direction} {category}: {trend_data['change_percent']:+.1f}% change") return True except Exception as e: print(f"ERROR: Error durante la preparaciÃ³n temporal: {e}") return False if __name__ == "__main__": success = prepare_temporal_data() if success: print("\nOK: TEMPORAL DATA PREPARATION COMPLETED") else: print("\nERROR: ERROR IN TEMPORAL DATA PREPARATION") 